import { NAME, F_LASTNAME, M_LASTNAME, EMAIL, DATE } from "./actionType";

const INIT_STATE = {
    /* city: JSON.parse(localStorage.getItem("city")) || {} */
    NAME: "",
    F_LASTNAME: "",
    M_LASTNAME: "",
    EMAIL: "",
    DATE: null
}

const user = (state = INIT_STATE, action) => {
    switch(action.type){
        case NAME: 
            return {
                ...state,
                NAME: action.data
            }
        case F_LASTNAME: 
            return {
                ...state,
                F_LASTNAME: action.data
            }
        case M_LASTNAME: 
            return {
                ...state,
                M_LASTNAME: action.data
            }
        case EMAIL: 
            return {
                ...state,
                EMAIL: action.data
            }
        case DATE: 
            return {
                ...state,
                DATE: action.data
            }
        default:
            return state
    }
}

export default user;
